def distorsionar(ejemplo, porcentaje):

    df = copy.copy(ejemplo)

    cantCeldasDistorsion = round(len(df)*porcentaje)
  
    # primero obtengo los indices que estan en el contorno de la letra

    restaCol = [0, 10, -10]
    restaFila = [0, 1, -1]

    indicesPosibles = []

    for i, elem in enumerate(df):
        if elem == 1:

            for r in restaCol:
                for r2 in restaFila:

                    newIx = i + r + r2
                if newIx not in indicesPosibles:
                    indicesPosibles.append(newIx)
    # luego elijo del conjunto de indices posibles una posicion random y a ese indice le cambio su valor en el arreglo principal (df)
    index = 0
    ultimaPos = len(indicesPosibles) - 1
    posicionesUsadas = []

    while index < cantCeldasDistorsion:
        p = random.randrange(0, ultimaPos)
        if p not in posicionesUsadas:
            ix = indicesPosibles[p]

        if df[ix] == 1:
            df[ix] = 0
        else:
            df[ix] = 1

        posicionesUsadas.append(p)
        index = index + 1

    return df


