def dsigmoide(x):
    s = 1/(1+np.exp(-x))
    return s * (1-s)


def errorGlobalPorEpoca(entrada, pesosCapaOculta, pesosCapaSalida):
    errorGlobal = 0
    for iter in range(len(entrada)):
        entradaActual = entrada[iter][0:100]
        salidaDeseada = entrada[iter][100:103]
        salidaObtenida, entradaNetaCapaSalida, salidaCapaOculta = redNeuronal1(
            entradaActual, pesosCapaOculta, pesosCapaSalida)
        errorDelPatron = 0
        for i in range(3):
            errorDelPatron += math.pow(
                int(salidaDeseada[i]) - salidaObtenida[i], 2)
        errorDelPatron = errorDelPatron/2
        errorGlobal += errorDelPatron
    errorGlobal = errorGlobal/len(entrada)
    return errorGlobal


"""Realiza la propagación hacia adelante"""
# entrada es un patron


def redNeuronal1(entrada, pesosCapaOculta, pesosCapaSalida):

    # Salida capa oculta sumatoria de los pesos por entrada de la capa anterior(capa de entrada)
    salidaCapaOculta = np.dot(pesosCapaOculta, np.transpose(entrada))

    # Capa de salida en entradaNetaCapaSalida se almacena sumatoria de los pesos por la salida de la capa anterior()salidaCapaOculta
    entradaNetaCapaSalida = np.dot(
        pesosCapaSalida, np.transpose(salidaCapaOculta))
    salidaCapaSalida = np.zeros(entradaNetaCapaSalida.shape[0])
    # Valores de las tres neuronas de capa de salida
    for i in range(0, entradaNetaCapaSalida.shape[0]):
        salidaCapaSalida[i] = sigmoide(entradaNetaCapaSalida[i])

    return salidaCapaSalida, entradaNetaCapaSalida, salidaCapaOculta
