         listMomentPesosCo1.append(pesosCapaOculta1)
         patron=patron+1
        
         #Comprobar finalizacion de una epoca de entrenamiento
         if  patron >=len(entrada):
            epocas=epocas+1
            #Agrego a la lista el errorGlobal de entrenamiento y divido por la cantidad de entrenamiento
