def sigmoide(x):
    return 1/(1+np.exp(-x))


def redNeuronal(entrada, pesosCapaOculta, pesosCapaSalida):
    # Capa entrada no realiza ningun procesamiento
    entradaNetaCapaOculta = np.dot(pesosCapaOculta, np.transpose(entrada))
    salidaCapaOculta = np.zeros(entradaNetaCapaOculta.shape[0])
    # Valores de las neuronas de capa oculta
    for i in range(0, entradaNetaCapaOculta.shape[0]):
        salidaCapaOculta[i] = entradaNetaCapaOculta[i]

    # Capa de salida
    entradaNetaCapaSalida = np.dot(
        pesosCapaSalida, np.transpose(salidaCapaOculta))
    salidaCapaSalida = np.zeros(entradaNetaCapaSalida.shape[0])
    # Valores de las tres neuronas de capa de salida
    for i in range(0, entradaNetaCapaSalida.shape[0]):
        salidaCapaSalida[i] = round(sigmoide(entradaNetaCapaSalida[i]), 4)

    return salidaCapaSalida, entradaNetaCapaOculta, entradaNetaCapaSalida, salidaCapaOculta


def resource_path(relative_path):
    """ Get absolute path to resource, works for dev and for PyInstaller """
    try:
        # PyInstaller creates a temp folder and stores path in _MEIPASS
        base_path = sys._MEIPASS
    except Exception:
        base_path = os.path.abspath(".")

    return os.path.join(base_path, relative_path)

def redNeuronalDosCapas (entrenamiento,pesosCapaOculta1,pesosCapaOculta2,pesosCapaSalida):
  #Primer capa oculta
  salidaCapaOculta1=np.dot(pesosCapaOculta1,np.transpose(entrenamiento))

  #Segunda capa oculta
  salidaCapaOculta2=np.dot(pesosCapaOculta2,salidaCapaOculta1)

  #Capa de salida
  entradaNetaCapaSalida= np.dot(pesosCapaSalida,np.transpose(salidaCapaOculta2))
  salidaCapaSalida = np.zeros(entradaNetaCapaSalida.shape[0])
  #Valores de las tres neuronas de capa de salida
  for i in range(0,entradaNetaCapaSalida.shape[0]):
    salidaCapaSalida[i]=sigmoide(entradaNetaCapaSalida[i])

  return salidaCapaSalida,entradaNetaCapaSalida,salidaCapaOculta1,salidaCapaOculta2


